async function handleEmailSubmit(e){e.preventDefault();const s=e.target,t=s.querySelector('input[type="email"]'),n=s.querySelector('button[type="submit"]'),o=(document.getElementById("subscriptionMessage"),t.value.trim());if(o){n.disabled=!0,n.textContent="Sending...";try{await sendToDiscord(o),showMessage("Thank you for subscribing! You will be notified when CIN Framework launches.","success"),t.value=""}catch(e){showMessage("Something went wrong. Please try again later.","error")}finally{n.disabled=!1,n.textContent="Notify Me"}}else showMessage("Please enter a valid email address.","error")}async function sendToDiscord(e){const s={embeds:[{title:"🚀 New CIN Framework Early Access Subscription",description:"A new user has subscribed for early access to CIN Framework!",color:65280,fields:[{name:"📧 Email",value:e,inline:!1},{name:"⏰ Timestamp",value:(new Date).toISOString(),inline:!1},{name:"🌐 Source",value:"CIN Framework Website",inline:!1}],footer:{text:"CIN Framework Early Access System"}}]},t=await fetch("https://discord.com/api/webhooks/1404125958348341448/Be3r2x-xg8kLsOzhCestqF9HshyRryJnqQrGn72Dx7k9M-MdkxpejBO8kZp9J1crtgqO",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(s)});if(!t.ok)throw new Error(`Discord webhook failed: ${t.status}`)}function showMessage(e,s){const t=document.getElementById("subscriptionMessage");t.textContent=e,t.className="cin-alert cin-alert-"+("success"===s?"success":"error"),t.style.display="block",setTimeout((()=>{t.style.display="none"}),5e3)}